import React, { useEffect, useState } from 'react'
import DeleteModal from '../DeleteModal/DeleteModal'
import ErrorBox from '../Errorbox/Errorbox'
import DetailsModal from '../DetailsModal/DetailsModal'
import "./Comments.css"

export default function Comments() {
  const [allComments, setAllComments] = useState([])
  const [showTextComment, setShowTextComment] = useState(false)
  const [textComment, setTextComment] = useState('')
  const [isShowDeleteModal, setIsShowDeleteModal] = useState(false)
  const [CommentAction, setCommentAction] = useState(false)


  useEffect(() => {
    fetch('http://localhost:8000/api/comments/')
      .then(res => res.json())
      .then(Comments => setAllComments(Comments))

  }, [])

  const closeDleteModal=()=>setIsShowDeleteModal(false)

  const deleteComment=()=>{
    console.log('کامنت بسته شد');
    fetch('http://localhost:8000/api/comments/',{
      method:'DELETE'
    })

  }

  return (
    <div className='cms-main'>

      {
        allComments.length ? (
          <table className='cms-table'>
            <thead>
              <tr>
                <th>اسم کاربر</th>
                <th>محصول</th>
                <th>کامنت</th>
                <th>تاریخ</th>
                <th>ساعت</th>
              </tr>
            </thead>

            <tbody>
              {
                allComments.map(comment => (

                  <tr key={comment.id} >
                    <td>{comment.userID}</td>
                    <td>{comment.productID} </td>
                    <td>
                      <button onClick={()=>{
                        setShowTextComment(true)
                        setTextComment(comment.body)

                        }}>دیدن متن</button>
                      </td>
                    <td>{comment.data}</td>
                    <td>{comment.hour}</td>
                    <td>
                      <button onClick={()=>{
                        setIsShowDeleteModal(true)
                        setCommentAction(comment.id)
                      }}>حذف</button>
                      <button>ویرایش</button>
                      <button>پاسخ</button>
                      <button>تایید</button>
                    </td>
                  </tr>
                ))
              }

            </tbody>

          </table>
        ) : (
          <ErrorBox msg="هیچ کامنتی یافت نشد" />
        )

      }
      {
                showTextComment && (
                  <DetailsModal onHide={()=>setShowTextComment(false)}>
                    {textComment}
                    <button className='text-modal-close-btn' onClick={()=>setShowTextComment(false)}>بستن</button>
                  </DetailsModal >

                )
      }
      {
        isShowDeleteModal&&(
          <DeleteModal cancelAction={closeDleteModal} submitAction={deleteComment}/>
        )
      }


    </div>
  )
}
