import React, { useEffect, useState } from "react";
import ErrorBox from "../Errorbox/Errorbox";
import DeleteModal from './../DeleteModal/DeleteModal'
import EdutModal from './../EditModal/EditModal.js'
import { AiOutlineDollarCircle } from "react-icons/ai";

export default function Users() {
  const [users, setUsers] = useState([]);
  const [isShowDeleteModal, setIsShowDeleteModal] = useState(false)
  const [isShowEditModal, setIsShowEditModal] = useState(false)
  const [mainUserID, setMainUserID] = useState(null)
const [firsname ,setfirsname]=useState('')
const [lastname ,setlastname]=useState('')
const [username ,setusername]=useState('')
const [password ,setpassword]=useState('')
const [phone ,setphone]=useState('')
const [city ,setcity]=useState('')
const [email ,setemail]=useState('')
const [address ,setaddress]=useState('')
const [score ,setscore]=useState('')  
const [buy ,setbuy]=useState('')
  useEffect(() => {
    getAllUsers()
  }, []);

  function getAllUsers() {
    fetch(`http://localhost:8000/api/users`)
      .then((res) => res.json())
      .then((users) => setUsers(users));
  }

  const closeDeleteModal = () => setIsShowDeleteModal(false)

  const removeUser = () => {
    console.log(mainUserID);
    fetch(`http://localhost:8000/api/users/${mainUserID}`, {
      method: 'DELETE'
    }).then(res => res.json())
      .then(result => {
        console.log(result);
        setIsShowDeleteModal(false)
        getAllUsers()
      })

  }

  const editeUser = () => {
    let updataInfoUser = {
      firsname: {},
      lastname: {},
      username: {},
      password: {},
      phone: {},
      city: {},
      email: {},
      address: {},
      score: {},
      buy: {}
    }

}

return (
  <div className="cms-main">
    <h1 className="cms-title">لیست کاربران</h1>

    {users.length ? (
      <table className="cms-table">
        <thead>
          <tr>
            <th>نام و نام خانوادگی</th>
            <th>یوزرنیم</th>
            <th>رمز عبور</th>
            <th>شماره تماس</th>
            <th>ایمیل</th>
          </tr>
        </thead>

        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.firsname}-{user.lastname}</td>
              <td>{user.username}</td>
              <td>{user.password}</td>
              <td>{user.phone}</td>
              <td>{user.email}</td>
              <td>
                <button onClick={() => {
                  setIsShowDeleteModal(true)
                  setMainUserID(user.id)
                }}>حذف</button>
                <button>جزییات</button>
                <button onClick={() => setIsShowEditModal(true)}>ویرایش</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    ) : (
      <ErrorBox msg="هیچ کاربری یافت نشد" />
    )}
    {
      isShowDeleteModal && (
        <DeleteModal
          title="آیا از حذف اطمینان دارید؟"
          cancelAction={closeDeleteModal}
          submitAction={removeUser}
        />
      )
    }
    {
      isShowEditModal && <EdutModal onSubmit={editeUser} onClose={() => isShowEditModal(false)}>
        <div className="edit-user-info-input-group">
          <span>
            <AiOutlineDollarCircle />
          </span>
          <input type="text" className="edit-user-info-input " placeholder="  نام کاربر را وارد نمایید" />

        </div>
        <div className="edit-user-info-input-group">
          <span>
            <AiOutlineDollarCircle />
          </span>
          <input type="text" className="edit-user-info-input " placeholder="مقدار جدید را وارد نمایید" />

        </div>
        <div className="edit-user-info-input-group">
          <span>
            <AiOutlineDollarCircle />
          </span>
          <input type="text" className="edit-user-info-input " placeholder="مقدار جدید را وارد نمایید" />

        </div>
        <div className="edit-user-info-input-group">
          <span>
            <AiOutlineDollarCircle />
          </span>
          <input type="text" className="edit-user-info-input " placeholder="مقدار جدید را وارد نمایید" />

        </div>
        <div className="edit-user-info-input-group">
          <span>
            <AiOutlineDollarCircle />
          </span>
          <input type="text" className="edit-user-info-input " placeholder="مقدار جدید را وارد نمایید" />

        </div>
        <div className="edit-user-info-input-group">
          <span>
