import React, { useEffect, useState } from "react";
import "./ProductsTable.css";
import DeleteModal from "./../DeleteModal/DeleteModal";
import DetailsModal from "./../DetailsModal/DetailsModal";
import EditModal from "./../EditModal/EditModal";
import { AiOutlineDollarCircle } from "react-icons/ai";
import ErrorBox from "../Errorbox/Errorbox";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import { MdOutlineDriveFileRenameOutline } from "react-icons/md";
import { FaDollarSign } from "react-icons/fa";
import { BsBoxSeam } from "react-icons/bs";
import { FaLink } from "react-icons/fa";
import { FaRegStar } from "react-icons/fa";
import { BiCoinStack } from "react-icons/bi";
import { IoColorPaletteOutline } from "react-icons/io5";



export default function ProductsTable() {
  const [isShowDeleteModal, setIsShowDeleteModal] = useState(false);
  const [isShowDetailsModal, setIsShowDetailsModal] = useState(false);
  const [isShowEditModal, setIsShowEditModal] = useState(false);
  const [allProducts, setAllProducts] = useState([]);
  const [productID, setProductID] = useState(null)
  const [mainProductInfos, setMainProductInfos] = useState([])

  useEffect(() => {
    getAllProducts()
  }, []);

  const getAllProducts = () => {
    fetch("http://localhost:8000/api/products/")
      .then((res) => res.json())
      .then((products) => setAllProducts(products));
  }

  const notify = () => toast("محصول مورد نظر حذف شد");


  const deleteModalCancelAction = () => {
    console.log("مدال کنسل شد");
    setIsShowDeleteModal(false);

  };

  const deleteModalSubmitAction = () => {
    console.log("مدال تایید شد");
    fetch(`http://localhost:8000/api/products/${productID}`, {
      method: 'DELETE'
    }).then(res => res.json())
      .then(result => {
        setIsShowDeleteModal(false);
        getAllProducts()
        notify()
      })

  };

  const closeDetailsmodal = () => {
    setIsShowDetailsModal(false);
    console.log("مدال جزییات بسته شد");
  };

  const updateProductInfos = (event) => {
    event.preventDefault();
    console.log("محصول ویرایش شد");
  };

  return (
    <>
      <ToastContainer />

      {allProducts.length ? (
        <table className="products-table">
          <thead>
            <tr className="products-table-heading-tr">
              <th>عکس</th>
              <th>اسم</th>
              <th>قیمت</th>
              <th>موجودی</th>
            </tr>
          </thead>

          <tbody>
            {allProducts.map((product) => (
              <tr key={product.id} className="products-table-tr">
                <td>
                  <img
                    src={product.img}
                    alt="oil image"
                    className="products-table-img"
                  />
                </td>
                <td>{product.title}</td>
                <td>{product.price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")} تومان</td>
                <td>{product.count}</td>
                <td>
                  <button
                    className="products-table-btn"
                    onClick={() => {
                      setIsShowDetailsModal(true)
                      setMainProductInfos(product)
                    }}
                  >
                    جزییات
                  </button>
                  <button
                    className="products-table-btn"
                    onClick={() => {
                      setIsShowDeleteModal(true)
                      setProductID(product.id)
                    }}
                  >
                    حذف
                  </button>
                  <button
                    className="products-table-btn"
                    onClick={() => setIsShowEditModal(true)}
                  >
                    ویرایش
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <ErrorBox msg="هیچ محصولی یافت نشد" />
      )}

      {isShowDeleteModal && (
        <DeleteModal
          submitAction={deleteModalSubmitAction}
          cancelAction={deleteModalCancelAction}
        />
      )}
      {
        isShowDetailsModal && (<DetailsModal onHide={closeDetailsmodal}>
          <table className="cms-table">
            <thead>
              <tr>
                <th>رنگبندی</th>
                <th>فروش</th>
                <th>محبوبیت</th>
              </tr>
            </thead>

            <tbody>
              <tr>
                <td>{mainProductInfos.colors}</td>
                <td>{mainProductInfos.sale.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")}    تومان</td>
                <td>{mainProductInfos.popularity}%</td>
              </tr>
            </tbody>
          </table>
        </DetailsModal>
        )}
      {isShowEditModal && (
        <EditModal
          onClose={() => setIsShowEditModal(false)}
          onSubmit={updateProductInfos}
        >
          <div className="edit-proructs-form-group">
            <span>
              <MdOutlineDriveFileRenameOutline />
            </span>
            <input
              type="text"
              placeholder="عنوان جدید را وارد کنید"
              className="edit-product-input"
            />
          </div>
          <div className="edit-proructs-form-group">
            <span>
              <FaDollarSign />
            </span>
            <input
              type="text"
              placeholder="مبلغ جدید را وارد کنید"
              className="edit-product-input"
            />
          </div>
          <div className="edit-proructs-form-group">
            <span>
              <BsBoxSeam />
            </span>
            <input
              type="text"
              placeholder="موجودی جدید را وارد کنید"
              className="edit-product-input"
            />
          </div>
          <div className="edit-proructs-form-group">
